// authActions.js
import axios from 'axios';

export const registerUser = (formData) => async (dispatch) => {
  try {
    const response = await axios.post('/api/register', formData);
    console.log(response.data); // Manejar mensaje de éxito o redirigir al usuario
  } catch (error) {
    console.error('Error al registrar usuario:', error);
    // Manejar mensaje de error o mostrar error al usuario
  }
};

export const loginUser = (formData) => async (dispatch) => {
  try {
    const response = await axios.post('/api/login', formData);
    console.log(response.data); // Manejar mensaje de éxito o redirigir al usuario
  } catch (error) {
    console.error('Error al iniciar sesión:', error);
    // Manejar mensaje de error o mostrar error al usuario
  }
};

export const forgotPassword = (formData) => async (dispatch) => {
  try {
    const response = await axios.post('/api/forgot-password', formData);
    console.log(response.data); // Manejar mensaje de éxito o redirigir al usuario
  } catch (error) {
    console.error('Error al solicitar restablecimiento de contraseña:', error);
    // Manejar mensaje de error o mostrar error al usuario
  }
};

export const resetPassword = (formData, token) => async (dispatch) => {
  try {
    const response = await axios.post(`/api/reset-password/${token}`, formData);
    console.log(response.data); // Manejar mensaje de éxito o redirigir al usuario
  } catch (error) {
    console.error('Error al restablecer la contraseña:', error);
    // Manejar mensaje de error o mostrar error al usuario
  }
};
