import React, { useState } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:3001/conversations';

function ChatInput() {
  const [message, setMessage] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (message.trim() !== '') {
      try {
        const response = await axios.post(API_URL, { message });
        console.log(response.data); // Maneja la respuesta según lo necesites
        setMessage('');
      } catch (error) {
        console.error('Error sending message:', error);
      }
    }
  };

  return (
    <form onSubmit={handleSubmit} className="flex items-center">
      <input
        type="text"
        placeholder="Escribe tu mensaje..."
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        className="flex-1 px-4 py-2 rounded-md border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
      />
      <button type="submit" className="px-4 py-2 ml-2 rounded-md bg-blue-500 text-white hover:bg-blue-600">Enviar</button>
    </form>
  );
}

export default ChatInput;
