import { AUTH_ACTIONS } from '../actions/authActions';

const initialState = {
  isAuthenticated: false,
  user: null,
  token: null,
  error: null,
  loading: false,
  message: null,
};

const authReducer = (state = initialState, action) => {
  console.log("Auth Reducer - Action Type:", action.type);
  console.log("Auth Reducer - Current State:", state);

  switch (action.type) {
    case AUTH_ACTIONS.REGISTER_SUCCESS:
    case AUTH_ACTIONS.LOGIN_SUCCESS:
      console.log("Auth Reducer - Success Payload:", action.payload);
      return {
        ...state,
        isAuthenticated: true,
        user: action.payload.user,
        token: action.payload.token,
        error: null,
      };
    case AUTH_ACTIONS.UPDATE_PROFILE_SUCCESS:
      console.log("Auth Reducer - Update Profile Payload:", action.payload);
      return {
        ...state,
        user: action.payload.user,
        loading: false,
        error: null,
      };
    case AUTH_ACTIONS.REGISTER_FAILURE:
    case AUTH_ACTIONS.LOGIN_FAILURE:
    case AUTH_ACTIONS.UPDATE_PROFILE_FAILURE:
      console.log("Auth Reducer - Failure Payload:", action.payload);
      return {
        ...state,
        isAuthenticated: false,
        user: null,
        token: null,
        loading: false,
        error: action.payload.error,
      };
    case AUTH_ACTIONS.LOGOUT:
      return initialState;
    case AUTH_ACTIONS.FORGOT_PASSWORD_SUCCESS:
    case AUTH_ACTIONS.RESET_PASSWORD_SUCCESS:
      return {
        ...state,
        message: action.payload.message,
        error: null,
      };
    case AUTH_ACTIONS.FORGOT_PASSWORD_FAILURE:
    case AUTH_ACTIONS.RESET_PASSWORD_FAILURE:
      return {
        ...state,
        message: null,
        error: action.payload.error,
      };
    default:
      console.log("Auth Reducer - Default Case");
      return state;
  }
};

export default authReducer;
