// src/redux/actions/profileActions.js
import axios from 'axios';

export const PROFILE_ACTIONS = {
  FETCH_PROFILE_SUCCESS: 'FETCH_PROFILE_SUCCESS',
  FETCH_PROFILE_FAILURE: 'FETCH_PROFILE_FAILURE',
};

export const fetchUserProfile = () => async (dispatch, getState) => {
  const { token } = getState().auth;
  try {
    const response = await axios.get('http://localhost:3001/api/profile', {
      headers: { Authorization: `Bearer ${token}` },
    });
    dispatch({ type: PROFILE_ACTIONS.FETCH_PROFILE_SUCCESS, payload: response.data });
  } catch (error) {
    dispatch({ type: PROFILE_ACTIONS.FETCH_PROFILE_FAILURE, payload: error.message });
  }
};
