import React from 'react';
import { useSelector } from 'react-redux';

function HomePage() {
  // Obtenemos el usuario del estado de Redux
  const user = useSelector((state) => state.auth?.user);

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Verificamos si hay un usuario definido */}
      {user ? (
        <div className="text-center">
          {/* Mostramos el mensaje de bienvenida con el nombre del usuario */}
          <h1 className="text-3xl font-bold">Welcome to Weaving Stories, {user.username || 'User'}!</h1>
          {/* Verificamos si el usuario tiene una foto y la mostramos si est√° disponible */}
          {user.photo && <img src={user.photo} alt="User Avatar" className="w-32 h-32 rounded-full mx-auto mt-4" />}
        </div>
      ) : (
        <div className="text-center">
          {/* Mensaje de bienvenida predeterminado si no hay usuario definido */}
          <h1 className="text-3xl font-bold">Welcome to Weaving Stories!</h1>
          <p className="text-lg">This is the home page.</p>
        </div>
      )}
    </div>
  );
}

export default HomePage;
