import React, { useState } from 'react';

function ProfilePage() {
  const [nickname, setNickname] = useState('');
  const [bio, setBio] = useState('');
  const [newPost, setNewPost] = useState('');
  const [posts, setPosts] = useState([]);

  const handleNicknameChange = (e) => {
    setNickname(e.target.value);
  };

  const handleBioChange = (e) => {
    setBio(e.target.value);
  };

  const handlePostChange = (e) => {
    setNewPost(e.target.value);
  };

  const handleSubmitPost = (e) => {
    e.preventDefault();
    if (newPost.trim() !== '') {
      const newPostObject = {
        id: posts.length + 1,
        content: newPost,
        likes: 0,
        comments: [],
      };
      setPosts([newPostObject, ...posts]);
      setNewPost('');
    }
  };

  const handleLikePost = (postId) => {
    const updatedPosts = posts.map((post) =>
      post.id === postId ? { ...post, likes: post.likes + 1 } : post
    );
    setPosts(updatedPosts);
  };

  const handleAddComment = (postId, commentText) => {
    const updatedPosts = posts.map((post) =>
      post.id === postId
        ? { ...post, comments: [...post.comments, commentText] }
        : post
    );
    setPosts(updatedPosts);
  };

  const handleDeletePost = (postId) => {
    const updatedPosts = posts.filter((post) => post.id !== postId);
    setPosts(updatedPosts);
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-4">Bienvenido a mi perfil</h1>
      
      {/* Sección de Información de Perfil */}
      <div className="mb-8">
        <h2 className="text-xl font-semibold mb-2">Información de Perfil</h2>
        <div className="flex items-center mb-4">
          <label htmlFor="nickname" className="mr-2">Apodo:</label>
          <input
            type="text"
            id="nickname"
            value={nickname}
            onChange={handleNicknameChange}
            className="border border-gray-300 rounded px-2 py-1"
          />
        </div>
        <div className="flex items-center mb-4">
          <label htmlFor="bio" className="mr-2">Biografía:</label>
          <textarea
            id="bio"
            value={bio}
            onChange={handleBioChange}
            className="border border-gray-300 rounded px-2 py-1"
          />
        </div>
      </div>
      
      {/* Sección de Publicaciones */}
      <div className="mb-8">
        <h2 className="text-xl font-semibold mb-4">Publicaciones</h2>
        <form onSubmit={handleSubmitPost} className="mb-4">
          <textarea
            value={newPost}
            onChange={handlePostChange}
            placeholder="Escribe tu publicación..."
            className="w-full border border-gray-300 rounded px-2 py-1"
          />
          <button type="submit" className="bg-blue-500 text-white py-1 px-4 rounded mt-2 hover:bg-blue-600">
            Publicar
          </button>
        </form>
        {posts.map((post) => (
          <div key={post.id} className="border border-gray-300 rounded p-4 mb-4">
            <p>{post.content}</p>
            <div className="flex items-center mt-2">
              <button onClick={() => handleLikePost(post.id)} className="text-blue-500 hover:underline mr-2">
                Me gusta ({post.likes})
              </button>
              <button className="text-blue-500 hover:underline mr-2">
                Comentar
              </button>
              <button onClick={() => handleDeletePost(post.id)} className="text-red-500 hover:underline">
                Eliminar
              </button>
            </div>
            <div className="mt-2">
              {post.comments.map((comment, index) => (
                <p key={index} className="border border-gray-300 rounded p-2 mb-2">{comment}</p>
              ))}
              <form onSubmit={(e) => { e.preventDefault(); handleAddComment(post.id, e.target.elements.comment.value); e.target.elements.comment.value = '' }} className="flex items-center">
                <input type="text" name="comment" className="border border-gray-300 rounded px-2 py-1 mr-2" />
                <button type="submit" className="bg-blue-500 text-white py-1 px-4 rounded hover:bg-blue-600">
                  Agregar Comentario
                </button>
              </form>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProfilePage;
