// src/views/Profile/ProfilePage.jsx
import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchUserProfile } from '../../redux/actions/profileActions';

const ProfilePage = () => {
  const dispatch = useDispatch();
  const { userProfile, loading, profileError } = useSelector((state) => state.auth);

  useEffect(() => {
    dispatch(fetchUserProfile());
  }, [dispatch]);

  if (loading) return <div>Loading...</div>;
  if (profileError) return <div>Error: {profileError}</div>;

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-4">Perfil de Usuario</h1>
      <div className="bg-white shadow-md rounded-lg p-4">
        <h2 className="text-2xl font-semibold mb-2">{userProfile.username}</h2>
        <p className="text-lg mb-2">Email: {userProfile.email}</p>
        {/* Aquí puedes agregar más detalles del perfil del usuario */}
      </div>
    </div>
  );
};

export default ProfilePage;
