import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { updateProfile } from '../../redux/actions/authActions';

function ProfilePage() {
  const dispatch = useDispatch();
  const authState = useSelector(state => state.auth);
  const { user, loading } = authState || {};
  const [nickname, setNickname] = useState(user?.username || '');
  const [bio, setBio] = useState(user?.bio || '');
  const [profileImage, setProfileImage] = useState(null);

  useEffect(() => {
    if (user) {
      console.log('Usuario cargado:', user); // Debugging
      setNickname(user.username);
      setBio(user.bio);
    } else {
      console.log('Usuario no encontrado en el estado.'); // Debugging
    }
  }, [user]);

  const handleNicknameChange = (e) => {
    setNickname(e.target.value);
  };

  const handleBioChange = (e) => {
    setBio(e.target.value);
  };

  const handleProfileImageChange = (e) => {
    setProfileImage(e.target.files[0]);
  };

  const handleSubmitProfile = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('username', nickname);
    formData.append('bio', bio);
    if (profileImage) {
      formData.append('profileImage', profileImage);
    }
    dispatch(updateProfile(formData))
      .then(response => {
        console.log('Perfil actualizado:', response);
      })
      .catch(error => {
        console.error('Error al actualizar perfil:', error);
      });
  };

  if (loading) {
    return <div>Cargando perfil...</div>;
  }

  if (!user) {
    return <div>No se encontró el perfil.</div>;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-4">Perfil de Usuario</h1>

      <div className="mb-8">
        <h2 className="text-xl font-semibold mb-2">Información de Perfil</h2>
        <form onSubmit={handleSubmitProfile}>
          <div className="flex items-center mb-4">
            <label htmlFor="nickname" className="mr-2">Apodo:</label>
            <input
              type="text"
              id="nickname"
              value={nickname}
              onChange={handleNicknameChange}
              className="border border-gray-300 rounded px-2 py-1"
            />
          </div>
          <div className="flex items-center mb-4">
            <label htmlFor="bio" className="mr-2">Biografía:</label>
            <textarea
              id="bio"
              value={bio}
              onChange={handleBioChange}
              className="border border-gray-300 rounded px-2 py-1"
            />
          </div>
          <div className="flex items-center mb-4">
            <label htmlFor="profileImage" className="mr-2">Imagen de perfil:</label>
            <input
              type="file"
              id="profileImage"
              onChange={handleProfileImageChange}
              className="border border-gray-300 rounded px-2 py-1"
            />
          </div>
          <button type="submit" className="bg-blue-500 text-white py-1 px-4 rounded mt-2 hover:bg-blue-600">
            Actualizar perfil
          </button>
        </form>
      </div>

      <div className="mt-8">
        <h2 className="text-xl font-semibold mb-2">Detalles del Usuario</h2>
        <div>
          <p><strong>Apodo:</strong> {user.username}</p>
          <p><strong>Biografía:</strong> {user.bio}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Fecha de Registro:</strong> {user.createdAt ? new Date(user.createdAt).toLocaleDateString() : 'Fecha no disponible'}</p>
          <p><strong>Última Actualización:</strong> {user.updatedAt ? new Date(user.updatedAt).toLocaleDateString() : 'Fecha no disponible'}</p>
          <img src={user.profileImageUrl || '/default-profile-image.jpg'} alt="Imagen de perfil" className="w-32 h-32 rounded-full mt-4" />
        </div>
      </div>
    </div>
  );
}

export default ProfilePage;
