import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Asegúrate de que esté importado aquí
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { registerUser } from '../../redux/actions/authActions';

function RegisterPage() {
  const [formData, setFormData] = useState({ username: '', email: '', password: '' });
  const [showPassword, setShowPassword] = useState(false);
  const [passwordError, setPasswordError] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { username, email, password } = formData;

    if (!validatePassword(password)) {
      setPasswordError('La contraseña debe tener al menos 6 caracteres, una letra mayúscula y un número.');
      return;
    }

    dispatch(registerUser({ username, email, password }))
      .then(() => {
        navigate('/'); // Redirigir al login
      })
      .catch((error) => {
        const { response } = error;
        if (response) {
          const { status, data } = response;
          if (status === 400) {
            if (data.message.includes('correo electrónico')) {
              setPasswordError('El correo electrónico ya está registrado.');
            } else if (data.message.includes('nombre de usuario')) {
              setPasswordError('El nombre de usuario ya está registrado.');
            } else {
              setPasswordError(data.message || 'Error en el registro.');
            }
          } else {
            setErrorMessage('Error en el registro. Por favor intente de nuevo.');
          }
        } else {
          setErrorMessage('Error de red. Verifique su conexión y vuelva a intentarlo.');
        }
      });
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const validatePassword = (password) => {
    const regex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{6,}$/;
    return regex.test(password);
  };

  return (
    <div className="flex flex-col items-center">
      <h2 className="text-2xl font-bold mb-4">Registro</h2>
      <form onSubmit={handleSubmit} className="w-full max-w-xs">
        <input 
          type="text" 
          placeholder="Nombre de usuario" 
          name="username" 
          value={formData.username} 
          onChange={handleChange} 
          className="w-full border rounded py-2 px-3 mb-4" 
        />
        <input 
          type="text" 
          placeholder="Email" 
          name="email" 
          value={formData.email} 
          onChange={handleChange} 
          className="w-full border rounded py-2 px-3 mb-4" 
        />
        <div className="relative mb-4">
          <input 
            type={showPassword ? 'text' : 'password'} 
            placeholder="Password" 
            name="password" 
            value={formData.password} 
            onChange={handleChange} 
            className="w-full border rounded py-2 px-3" 
          />
          <label className="absolute inset-y-0 right-0 pr-3 flex items-center text-sm text-gray-600">
            <input 
              type="checkbox" 
              onChange={togglePasswordVisibility} 
              className="mr-1"
            />
            Mostrar contraseña
          </label>
        </div>
        {passwordError && <p className="mt-2 text-red-500">{passwordError}</p>}
        <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">Registro</button>
      </form>
      <p className="mt-4">¿Ya tienes una cuenta? <Link to="/" className="text-blue-500 hover:underline">Login</Link></p>
    </div>
  );
}

export default RegisterPage;
