import { PROFILE_ACTIONS } from '../actions/profileActions';

const initialState = {
  profile: null,
  error: null,
};

const profileReducer = (state = initialState, action) => {
  switch (action.type) {
    case PROFILE_ACTIONS.GET_PROFILE_SUCCESS:
      return {
        ...state,
        profile: action.payload,
        error: null,
      };
    case PROFILE_ACTIONS.GET_PROFILE_FAILURE:
      return {
        ...state,
        profile: null,
        error: action.payload,
      };
    case PROFILE_ACTIONS.UPDATE_PROFILE_SUCCESS:
      return {
        ...state,
        profile: action.payload,
        error: null,
      };
    case PROFILE_ACTIONS.UPDATE_PROFILE_FAILURE:
      return {
        ...state,
        error: action.payload,
      };
    default:
      return state;
  }
};

export default profileReducer;
